SELECT a.codigo as Pedido, a.data, b.nome, b.cpf, b.telefone, concat(c.logradouro,', ', c.numero, ', ', c.complemento,' ',c.ponto_referencia,', ', c.bairro, ', ',c.cep) as endereco, a.valor_compra, a.valor_entrega, a.valor_total, a.pagamento, pix_detalhes->>'$.id' as pix_operacao FROM vendas a left join clientes b on a.cliente = b.codigo left join enderecos c on a.endereco = c.codigo where a.codigo in (1455, 1450);